<?phpuse GDO\UI\GDT_ListItem;use GDO\UI\GDT_Button;use GDO\Guestbook\Module_Guestbook;use GDO\UI\GDT_Bar;/** @var $gdo \GDO\Guestbook\GDO_GuestbookMessage **/
$gb = $gdo->getGuestbook();$mod = Module_Guestbook::instance();$list = GDT_ListItem::make('gbm-' . $gdo->getID());$list->gdo($gdo);$list->titleCreation();$list->subtitle($gdo->gdoColumn('gbm_message'));if ($gdo->canApprove()){    $list->actions()->addField(GDT_Button::make('btn_approve')->href($gdo->hrefApprove()));}if ($gdo->canDelete()){    $confirmText = t('confirm_delete');    $deleteButton = GDT_Button::make('btn_delete')->attr('onclick', "return confirm('{$confirmText}')")->href($gdo->hrefDelete());    $list->actions()->addField($deleteButton);}$email = $website = null;if ($mod->cfgAllowEMail() && $gb->isEMailAllowed()){    $email = $gdo->gdoColumn('gbm_email');}if ($mod->cfgAllowURL() && $gb->isURLAllowed()){    $website = $gdo->gdoColumn('gbm_website');}if ($email->getVar() || $website->getVar()){    $box = GDT_Bar::make()->horizontal();    if ($email->getVar())    {        $box->addField($email);    }    if ($website->getVar())    {        $box->addField($website);    }    $list->subtext($box);}echo $list->renderCell();